interface BrandsCarouselProps {
  title?: string;
}

export function BrandsCarousel({ title }: BrandsCarouselProps) {
  const { t } = useTranslation();
  const [api, setApi] = useState<any>(null);

  // Auto-scroll functionality
  useEffect(() => {
    if (!api) return;

    const interval = setInterval(() => {
      if (api.canScrollNext()) {
        api.scrollNext();
      } else {
        api.scrollTo(0);
      }
    }, 5000);

    return () => clearInterval(interval);
  }, [api]);

  return (
    <div className="w-full">
      {title && <h2 className="text-xl md:text-2xl font-bold mb-4">{title}</h2>}

      <Carousel className="w-full" setApi={setApi}>
        <CarouselContent className="-ml-2 md:-ml-4">
          {techBrands.map((brand) => (
            <CarouselItem
              key={brand.id}
              className="pl-2 md:pl-4 basis-1/3 sm:basis-1/4 md:basis-1/5 lg:basis-1/6"
            >
              <div className="h-32 md:h-40 border rounded-lg flex flex-col items-center justify-center p-4 transition-all hover:shadow-lg bg-white dark:bg-gray-800">
                <div className="relative w-full h-24 md:h-32 mb-2">
                  <img
                    src={brand.logo}
                    alt={brand.name}
                    className="w-full h-full object-contain rounded-md"
                    onError={(e) => {
                      const target = e.target as HTMLImageElement;
                      target.src =
                        "https://upload.wikimedia.org/wikipedia/commons/thumb/9/9d/Generic_brand_logo.svg/1200px-Generic_brand_logo.svg.png";
                    }}
                  />
                </div>
                <span className="text-sm font-medium text-center">
                  {brand.name}
                </span>
              </div>
            </CarouselItem>
          ))}
        </CarouselContent>
        <div className="hidden sm:block">
          <CarouselPrevious className="left-2" />
          <CarouselNext className="right-2" />
        </div>
      </Carousel>
    </div>
  );
}
